mapper.select:
select * 
from receive_message
where receiver=#{username}
order by no desc
limit #{startIndex}, #{perPageNum}

select * 
from send_message
where sender=#{username}
order by no desc
limit #{startIndex}, #{perPageNum}

select count(*) 
from receive_message
where receiver=#{username}
and reading=#{reading}

select count(*) 
from receive_message
where receiver=#{username}

select count(*) 
from send_message
where sender=#{username}

mapper.update:
update send_message
set reading=#{reading}
where no=#{no}

update receive_message
set reading=#{reading}
where no=#{no}
